package java_prep;

public class Multiplication {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num = 7;
		for (int i =1; i<=10;i++) {
			int mul = num*i;
			System.out.println(mul);
		}
		System.out.println("In reverse order");
		for (int i =10; i>0;i--) {
			int mul = num*i;
			System.out.println(mul);
		}
		
	}
	

}
